const labelsResponse = $input.first().json;
const staticData = $getWorkflowStaticData('global');

const labelMap = {};
for (const l of (labelsResponse.labels || [])) {
  labelMap[l.name] = l.id;
}
staticData.labelMap = labelMap;
staticData.batchCount = 0;
staticData.totalProcessed = 0;
staticData.totalArchived = 0;

return { json: { labelCount: Object.keys(labelMap).length } };

const response = $input.first().json;
const messages = response.messages || [];
const resultSizeEstimate = response.resultSizeEstimate || 0;

if (messages.length === 0) {
  return [{ json: { done: true, remaining: 0 } }];
}

return messages.map(m => ({
  json: {
    messageId: m.id,
    threadId: m.threadId,
    done: false,
    remaining: resultSizeEstimate
  }
}));

const staticData = $getWorkflowStaticData('global');
return [{
  json: {
    message: 'Inbox Zero achieved!',
    totalProcessed: staticData.totalProcessed || 0,
    totalArchived: staticData.totalArchived || 0,
    batchesRun: staticData.batchCount || 0
  }
}];

const email = $input.first().json;

const headers = email.payload?.headers || [];
const getHeader = (name) => headers.find(h => h.name.toLowerCase() === name.toLowerCase())?.value || '';

const fromHeader = getHeader('From');
const senderMatch = fromHeader.match(/<([^>]+)>/) || fromHeader.match(/([\w.+-]+@[\w.-]+)/);
const senderEmail = senderMatch ? senderMatch[1].toLowerCase() : fromHeader.toLowerCase();
const senderDomain = senderEmail.split('@')[1] || '';
const subject = getHeader('Subject') || '(no subject)';

let body = '';
function extractText(part) {
  if (part.mimeType === 'text/plain' && part.body?.data) {
    return Buffer.from(part.body.data, 'base64').toString('utf-8');
  }
  if (part.parts) {
    for (const p of part.parts) {
      const text = extractText(p);
      if (text) return text;
    }
  }
  return '';
}
body = extractText(email.payload || {});
body = body.replace(/<[^>]*>/g, ' ');
body = body.split('\n').filter(line => !line.trim().startsWith('>')).join('\n');
body = body.replace(/\s+/g, ' ').trim();

// Sanitize for safe JSON interpolation
const sanitize = (str) => str.replace(/\\/g, '\\\\').replace(/"/g, '\\"').replace(/\n/g, ' ').replace(/\r/g, ' ').replace(/\t/g, ' ');

return {
  messageId: email.id,
  threadId: email.threadId,
  senderEmail,
  senderDomain,
  subject: sanitize(subject),
  bodyPreview: sanitize(body.substring(0, 1500)),
  snippet: email.snippet || ''
};

