const emailData = $('Preprocess').item;
const aiResponse = $input.first().json;

let classification;
try {
  const content = aiResponse.content?.[0]?.text || '';
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    classification = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found');
  }
} catch (e) {
  classification = {
    category: 'Other',
    priority: 'P2',
    action_needed: false,
    archive: false,
    confidence: 0.5,
    summary: 'Classification failed'
  };
}

return { ...emailData.json, ...classification };

const email = $input.first().json;
const staticData = $getWorkflowStaticData('global');
const labelMap = staticData.labelMap || {};

const labelNames = [];

const categoryMap = {
  'Finance': '#Finance',
  'Work': '#Work',
  'Personal': '#Personal',
  'Shopping': '#Shopping',
  'Travel': '#Travel',
  'Notifications': '#Notifications',
  'Newsletters': '#Newsletters',
  'Social': '#Social',
  'Receipts': '#Finance/Receipts',
  'Security': '#Security',
  'Other': '#Other'
};
labelNames.push(categoryMap[email.category] || '#Other');

if (email.priority === 'P1') {
  labelNames.push('!Priority/Urgent');
} else if (email.priority === 'P2' && email.action_needed) {
  labelNames.push('@Action/Review');
}

labelNames.push('_System/Processed');

if (email.confidence < 0.70) {
  labelNames.push('_System/Review');
}

const addLabelIds = labelNames
  .map(name => labelMap[name])
  .filter(id => id != null);

const shouldArchive = true;
const removeLabelIds = ['INBOX'];

// Track stats
staticData.totalProcessed = (staticData.totalProcessed || 0) + 1;
staticData.totalArchived = (staticData.totalArchived || 0) + 1;

return {
  messageId: email.messageId,
  subject: email.subject,
  category: email.category,
  priority: email.priority,
  confidence: email.confidence,
  summary: email.summary,
  archived: shouldArchive,
  labelNames,
  addLabelIds,
  removeLabelIds
};

// Batch complete - increment counter and loop back
const staticData = $getWorkflowStaticData('global');
staticData.batchCount = (staticData.batchCount || 0) + 1;

return [{ json: { 
  batchNumber: staticData.batchCount,
  totalProcessed: staticData.totalProcessed || 0,
  totalArchived: staticData.totalArchived || 0,
  message: `Batch ${staticData.batchCount} done. Processed ${staticData.totalProcessed} total. Checking for more...`
} }];

// Small delay between batches to avoid rate limits
await new Promise(resolve => setTimeout(resolve, 2000));
return $input.all();
