const error = $input.first().json;

const workflowName = error.workflow?.name || 'Unknown Workflow';
const workflowId = error.workflow?.id || '';
const errorMessage = error.execution?.error?.message || 'No error message';
const nodeName = error.execution?.lastNodeExecuted || 'Unknown Node';
const executionId = error.execution?.id || '';
const timestamp = new Date().toISOString();

const embed = {
  embeds: [
    {
      title: '\u26a0\ufe0f Workflow Error',
      color: 15548997,
      fields: [
        { name: 'Workflow', value: workflowName, inline: true },
        { name: 'Node', value: nodeName, inline: true },
        { name: 'Execution ID', value: executionId || 'N/A', inline: true },
        { name: 'Error', value: errorMessage.substring(0, 1024) }
      ],
      timestamp: timestamp,
      footer: { text: 'n8n Error Handler' }
    }
  ]
};

return [{ json: embed }];
